function textarea_MaxCharacterLimit(e,t,a){$("#"+a).text(e+" (0% used :: 0 characters used)"),$("#"+t).keyup(function(){let r=$(this).val(),c=r.replace(/\b(?:https?|ftp):\/\/[a-z0-9-+&@#\/%?=~_|!:,.;]*[a-z0-9-+&@#\/%=~_|]/gim,"").replace(/(^|[^\/])(www\.[\S]+(\b|$))/gim,"").replace(/[\w.]+@[a-zA-Z_-]+?(?:\.[a-zA-Z]{2,6})+/gim,"").replace(/(?<!\w)@([^\s]+)/,"").length;if(c>e)$("#"+t).val(r.substring(0,r.length-(c-e)));else{let t=Math.round(Math.ceil(100*c/e*20)/20);$("#"+a).text(e-c+" ("+t+"% used :: "+c+" characters used)")}})}
