<script>
    $(window).scroll(function () {
        if ($(this).scrollTop() > 700) {
            $(".back-to-top").fadeIn();
        } else {
            $(".back-to-top").fadeOut();
        }
    });
    $(".back-to-top").on('click', function () {
        $("html, body").animate({scrollTop: 0}, 700);
    });
</script>
