                            ctx.stroke();
                            ctx.beginPath();
                            ctx.moveTo(bp[i + 3].x, bp[i + 3].y);
                            if (bp[i + 3].newStrokeStyle)
                                ctx.strokeStyle = bp[i + 3].newStrokeStyle;
                            if (bp[i + 3].newLineDashArray)
                                ctx.setLineDash(bp[i + 3].newLineDashArray);
